# -*- coding: UTF-8 -*- class Animal(object):  # 父类    def run(self):  # 方法        print        'animal is running'class Dog(Animal):  # 继承父类,拥有父类的run()方法    def run(self):  # 重写父类的方法        print        'dog is running'    def eat(self):  # 增加方法.        print        'dog is eat'class Cat(Animal):    def run(self):        print        'cat is running'def run_twice(animal):    animal.run()    animal.run()class Tortoise(Animal):    def run(self):        print        'tortoise is running'dog = Dog()dog.eat()cat = Cat()cat.run()run_twice(Tortoise())run_twice(Animal())run_twice(Dog())run_twice(Cat())